import React, { useState, useContext, useEffect } from 'react';
import { StyleSheet, Text, View, Button, TextInput, TouchableOpacity } from 'react-native';
import { colores, sizes } from '../../estilos/estilos';
import UsuarioContext from '../../contexto/UsuarioContext';
import Mensaje from '../../componentes/Mensaje';
import DropDownPicker from 'react-native-dropdown-picker';

const AgregarPedidosLlevar = () => {
    const [idpedido, setIdpedido] = useState("");
    const [idcliente, setIdcliente] = useState("");
    return (
        <View style={styles.contenedor}>
            <View style={styles.contenedorInputs}>
                <TextInput
                placeholder="Ingrese el Id del pedido"
                style={styles.entradas}
                value={idpedido}
                onChangeText={setIdpedido}
                autoFocus={true}
                >
                </TextInput>
                <TextInput
                placeholder="Ingrese el Id del cliente"
                style={styles.entradas}
                value={idcliente}
                onChangeText={setIdcliente}
                autoFocus={false}
                >
                </TextInput>

            </View>
            <View style={styles.contenedorBoton}> 
            <TouchableOpacity onPress={()=>navigation.navigate('agregarPedidosLlevar')}>
            <Text style={styles.item}>Agregar Registro</Text>
        </TouchableOpacity>
              </View>
        </View>


    )
}; 
const styles = StyleSheet.create({
    contenedor: {
        backgroundColor: colores.gris600,
        alignItems: 'center',
        justifyContent: "center",
        margin:0,
        padding: 20,
        width:"100%",
        height:"100%",
    },

    contenedorInputs: {
        width: '90%',
        height: '60%',
        justifyContent: 'space-evenly'

    },
    entradas: {
      padding:15,
      fontSize: 20,
      fontWeight:"400",
      color: "#495057",
      backgroundColor:"#fff",
      borderWidth:1,
      borderStyle:"solid",
      borderColor: "#ced4da",
      borderRadius: 15,
    },
    contenedorBoton: {

        borderColor: '#bbb',
        borderWidth: 1,
        borderStyle: 'dashed',
        borderRadius: 1,
      },

        item: {
          backgroundColor: colores.gris800,
          padding: 18,
          color: 'white',   
    }

})

export default AgregarPedidosLlevar;
