import React, { useState, useContext, useEffect } from 'react';
import { StyleSheet, Text, View, Keyboard, TextInput, TouchableOpacity, TouchableWithoutFeedback, FlatList, ScrollView } from 'react-native';
import { colores, sizes } from '../../estilos/estilos';
import UsuarioContext from '../../contexto/UsuarioContext';
import Mensaje from '../../componentes/Mensaje';
import Axios from '../../componentes/Axios';
import CardPedidosLlevar from '../pedidosLlevar/CardPedidosLlevar';
import { render } from 'react-dom';

const ListarPedidosLlevar = () => {
    var textoMensaje = "";
    const { token } = useContext(UsuarioContext);
    const [lista, setLista] = useState("");
    useEffect(() => {
        buscarPedidosLlevar();
    }, [setLista]);

    const PedidosLlevarRender = ({item}) => {
        return(
            <CardPedidosLlevar idregistro={item.idregistro} idpedido={item.idpedido} idcliente={item.idcliente}>

            </CardPedidosLlevar>
        );
    };

    const buscarPedidosLlevar = async () => {
         try {
             await Axios.get('/pedidos/pedidosllevar/listar', {
                 headers: {
                    'Authorization': 'Bearer ' + token
                 }
             })
                 .then((data) => {
                    const json = data.data;

                    setLista(json);
                         console.log(lista)

                        // else {
                        //      textoMensaje = '';
                        //      json.errores.forEach(element => {
                        //          textoMensaje += element.mensaje + '. ';
                        //      });

                        //  }
                 })

    //             .catch((error) => {
    //                 textoMensaje = error;
    //                 Mensaje({ titulo: 'Error registro', msj: textoMensaje });
    //             });
         } catch (error) {
    //         textoMensaje = error;
    //         console.log(error);
    //         Mensaje({ titulo: 'Error registro', msj: error });
        }
    }

    return (
        <View style={styles.contenedor}>

                <View style={styles.contenedorTitulo}>
                    <Text style={styles.tituloLogin}>Lista Pedidos Llevar</Text>
                </View>
                <TouchableWithoutFeedback onPress={() => {
      Keyboard.dismiss();
    }}>
      <View style={styles.container}>
      <View style={styles.content}>

        <View style={styles.list}>
          <FlatList
            data={lista}
            renderItem={({item}) => (
              <CardPedidosLlevar item={item} />
            )}
          />
        </View>
      </View>
    </View>
    </TouchableWithoutFeedback>



        </View>
    )
}

const styles = StyleSheet.create({
    contenedor: {
        backgroundColor: colores.gris600,
        //alignItems: 'center',
        //justifyContent: "center",
        margin: 0,
        padding: 20,
        width: "100%",
        height: "100%",
    },

    contenedorTitulo: {
        height: 80,
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "flex-end",
    },
    list: {
        marginTop: 20
      },

    tituloLogin: {
        color: colores.textoTitulo,
        fontSize: sizes.fontTitulo,
        fontWeight: "500",
    },
    boton: {
        //flex: 1,
        alignItems: "stretch",
        marginLeft: 10,
        marginRight: 10,
    },
    botonRedes: {
        //flex:1,
        alignItems: "stretch",
        margin: 5,
    },

})

export default ListarPedidosLlevar;
