import React, { useState, useContext, useEffect } from 'react';
import { StyleSheet, Text, View, Button, TextInput, TouchableOpacity } from 'react-native';
import { colores, sizes } from '../../estilos/estilos';
import UsuarioContext from '../../contexto/UsuarioContext';
import Mensaje from '../../componentes/Mensaje';

const AgregarPedidosLlevar = () => {
    const [idpedido, setIdpedido] = useState("");
    const [idcliente, setIdcliente] = useState("");
    return (
        <View style={styles.contenedor}>
            <View style={styles.contenedorInputs}>


                <View style={styles.controles}>
                    <TextInput
                    placeholder="Ingrese el id del pedido"
                    style={styles.entradas}
                    value={idpedido}
                    onChangeText={setIdpedido}
                    autoFocus={true}
                    >
                    </TextInput>
                    <TextInput
                    placeholder="Ingrese el id del pedido"
                    style={styles.entradas}
                    value={idcliente}
                    onChangeText={setIdcliente}
                    autoFocus={false}
                    >
                    </TextInput>

                </View>
            </View>
        </View>


    )
}; 
const styles = StyleSheet.create({
    contenedor: {
        backgroundColor: colores.gris600,
        alignItems: 'center',
        justifyContent: "center",
        margin:0,
        padding: 20,
        width:"100%",
        height:"100%",
    },
    controles: {
        justifyContent:'space-evenly',
        height: '20%',
        color: 'white',
        backgroundColor: 'red',
        marginBottom: sizes.margin1,
        paddingTop:sizes.padding1,
        paddingLeft:sizes.padding1,
        paddingRight:sizes.padding1,
      },
    contenedorInputs: {
        backgroundColor: 'coral',
        width: '90%',
        height: '60%'

    },
    entradas: {
        marginTop:8,
      padding:10,
      fontSize: 20,
      fontWeight:"400",
      color: "#495057",
      backgroundColor:"#fff",
      borderWidth:1,
      borderStyle:"solid",
      borderColor: "#ced4da",
      borderRadius: 15,
    }

})

export default AgregarPedidosLlevar;
